import Layout from './components/MyLayout';
import cheerio from 'cheerio';
import axios from 'axios';
import NameForm from './components/NameForm';
import React, {useState} from 'react';


const layoutStyle = {
  marginTop: 10,
  marginBottom: 20,
  padding: 20,
  border: '1px solid #DDD'
};



// client side
export default (props) => {
  const [message, setMessage] = useState('')
  const callbackFunction = (childData) => {
    setMessage(childData)
  };
  // console.log(props)
  let key=0;
  return (
    <Layout content={
      <div style={layoutStyle}>
        {/* <NameForm parentCallback={callbackFunction}/> */}
        {/* {message} */}
        <h2>Result</h2>
        {props.urlsImgId1.map((url)=>(
         <img src={url} alt="" key={key++}/>
        ))}
        {props.urlsImgId2.map((url)=>(
         <img src={url} alt="" key={key++}/>
        ))}
      </div>
    }/>  
  )
}

// server side
export async function getServerSideProps(){
  let URL = 'https://tuoitre.vn/kinh-doanh.htm'
  let resFromUrl = await axios.get(URL)

  let $ = cheerio.load(resFromUrl.data)
  let links = [];
  $('.list-news-content')
    .find('li.news-item')
    .find('a.pos-rlt')
    .find('img.img212x132')
    .each((index, element)=>{
      links.push($(element).attr("src"))
    })
  const urlsImgId1 = links.filter(n => n != undefined )
  /****************************
   * 
   */
  URL = 'https://thanhnien.vn/tai-chinh-kinh-doanh/'
  resFromUrl = await axios.get(URL)
  
  $ = cheerio.load(resFromUrl.data)
  $('.l-content')
  .find('div.highlight')
  .find('article.story')
  .find('a.story__thumb')
  .find('img.loading')
  .each((index, element)=>{
    links.push($(element).attr('src'))
  })

  $('.l-content')
  .find('div.feature')
  .find('article.story')
  .find('a.story__thumb')
  .find('img.loading')
  .each((index, element)=>{
    links.push($(element).attr('src'))
  })
  const urlsImgId2 = links.filter(n => n != undefined )


  return {
    props: {urlsImgId1,urlsImgId2}
  }  

}
