import Layout from './components/MyLayout';
import cheerio from 'cheerio';
import axios from 'axios';
import React, {useState} from 'react';
import Post from './components/Post'


const layoutStyle = {
  marginTop: 10,
  marginBottom: 20,
  padding: 20,
  border: '1px solid #DDD'
};
const postStyle={
  border: '1px solid #ddd',
  borderRadius: '4px',
  padding: '5px',
  width: '200px',
};

/****
 * Bat dau config
 */

 const myConfig = {
   webSite: {
     muctim: {
        url: 'http://teen360.muctim.com.vn/',
        parent: '',
        child: '',
     },
    },
 }



// client side
export default ({props1, props2, props3}) => {
  const [message, setMessage] = useState('')
  const callbackFunction = (childData) => {
    setMessage(childData)
  };
  console.log({props1, props2, props3})
  const props = props1.map((title, index)=>({title: title, img: props2[index], key: index}))
  console.log(props);
  let key=0;
  return (
    <Layout content={
      <div style={layoutStyle}>
        {/* <NameForm parentCallback={callbackFunction}/> */}
        {/* {message} */}
        <h2>Result</h2>
        {props.map((url)=>(
         <Post title={url.title} img={url.img} key={url.key} style={postStyle}/>
        ))}
      </div>
    }/>  
  )
}

// server side
export async function getServerSideProps(){
  let URL = myConfig.webSite.muctim.url
  let resFromUrl = await axios.get(URL)

  let $ = cheerio.load(resFromUrl.data)
  let links=[];
  let title = [];
  let time = [];
  let imgs=[];
  let description = [];
  $('.clearfix')
  .each((index, element)=>{
    $(element)
    .find('article')
    .find('span')
    .each((index, element)=>{
      time.push($(element).text())
    })
  })
  .find('ul')
  .find('li')
  .find('a')
  .each((index, element)=>{
    $(element)
    .find('strong')
    .each((index, element)=>{
      title.push($(element).text())
    })
  })
  .find('div')
  .each((index, element)=>{
    imgs.push($(element).attr('data-lazy'))
  })
  const urlsImgId1 = title.filter(n => n != undefined )
  const urlsImgId2 = imgs.filter(n => n != undefined )
  const urlsImgId3 = time.filter(n => n != undefined )

  return {
    props: {
      props1: urlsImgId1,
      props2: urlsImgId2,
      props3: urlsImgId3
    }
  }  

}
