import Layout from './components/MyLayout';
import cheerio from 'cheerio';
import axios from 'axios';
import NameForm from './components/NameForm';
import React, {useState} from 'react';
import Post from './components/Post'


const layoutStyle = {
  marginTop: 10,
  marginBottom: 20,
  padding: 20,
  border: '1px solid #DDD'
};



// client side
export default ({props1, props2}) => {
  const [message, setMessage] = useState('')
  const callbackFunction = (childData) => {
    setMessage(childData)
  };
  console.log({props1, props2})
  let key=0;
  return (
    <Layout content={
      <div style={layoutStyle}>
        {/* <NameForm parentCallback={callbackFunction}/> */}
        {/* {message} */}
        <h2>Result</h2>
        {/* {props1.map((url)=>(
         <img src={url} alt="" key={key++}/>
        ))}
        {props2.map((url)=>(
         <img src={url} alt="" key={key++}/>
        ))} */}
        <Post />
      </div>
    }/>  
  )
}

// server side
export async function getServerSideProps(){
  let URL = 'https://tuoitre.vn/kinh-doanh.htm'
  let resFromUrl = await axios.get(URL)

  let $ = cheerio.load(resFromUrl.data)
  let links = [];
  $('.list-news-content')
    .find('li.news-item')
    .find('a.pos-rlt')
    .find('img.img212x132')
    .each((index, element)=>{
      //links.push($(element).attr("src"))
    })
  const urlsImgId1 = links.filter(n => n != undefined )
  /****************************
   * 
   */
  URL = 'http://teen360.muctim.com.vn/'
  resFromUrl = await axios.get(URL)
  links=[]
  $ = cheerio.load(resFromUrl.data)
  $('.clearfix')
  .find('ul')
  .find('li')
  .find('a')
  .find('div')
  .each((index, element)=>{
    links.push($(element).attr('data-lazy'))
  })
  const urlsImgId2 = links.filter(n => n != undefined )


  return {
    props: {
      props1: urlsImgId1,
      props2: urlsImgId2
    }
  }  

}
