import Layout from './components/MyLayout';
import cheerio from 'cheerio';
import axios from 'axios';
import NameForm from './components/NameForm';


const layoutStyle = {
  marginTop: 10,
  marginBottom: 20,
  padding: 20,
  border: '1px solid #DDD'
};


// client side
export default (props) => {
  // console.log(props)
  let key=0;
  return (
    <Layout content={
      <div style={layoutStyle}>
        <NameForm/>
        <h2>Result</h2>
        {props.urlsImg.map((url)=>(
          <img src={url} alt="" key={key++}/>
        ))}
      </div>
    }/>  
  )
}

// server side
export async function getServerSideProps(context){
  const URL = 'https://tuoitre.vn/kinh-doanh.htm'
  const resFromUrl = await axios.get(URL)

  const $ = cheerio.load(resFromUrl.data)
  let links = [];
  $('.list-news-content')
    .find('li.news-item')
    .find('a.pos-rlt')
    .find('img.img212x132')
    .each((index, element)=>{
      links.push($(element).attr("src"))
    })
    const urlsImg = links.filter(n => n != undefined )
    return {
      props: {urlsImg}
    }  

}
