import Layout from './components/MyLayout';
import cheerio from 'cheerio';
import axios from 'axios';
import NameForm from './components/NameForm';
import React, {useState} from 'react';
import Post from './components/Post'


const layoutStyle = {
  marginTop: 10,
  marginBottom: 20,
  padding: 20,
  border: '1px solid #DDD'
};



// client side
export default ({props}) => {
  const [message, setMessage] = useState('')
  const callbackFunction = (childData) => {
    setMessage(childData)
  };
  console.log({props})
  let key=0;
  return (
    <Layout content={
      <div style={layoutStyle}>
        {/* <NameForm parentCallback={callbackFunction}/> */}
        {/* {message} */}
        <h2>Result</h2>
        {/* {props1.map((url)=>(
         <img src={url} alt="" key={key++}/>
        ))} */}
        {props.map((url)=>(
         <Post title={url.props1.title} /*description={url.description}*/ img={url.props2.img} /*time={url.time}*/ />
        ))}
      </div>
    }/>  
  )
}

// server side
export async function getServerSideProps(){
  let URL = 'https://tuoitre.vn/kinh-doanh.htm'
  let resFromUrl = await axios.get(URL)

  let $ = cheerio.load(resFromUrl.data)
  let links=[];
  // $('.list-news-content')
  //   .find('li.news-item')
  //   .find('a.pos-rlt')
  //   .find('img.img212x132')
  //   .each((index, element)=>{
  //     //links.push($(element).attr("src"))
  //   })
  // const urlsImgId1 = links.filter(n => n != undefined )
  /****************************
   * 
   */
  URL = 'http://teen360.muctim.com.vn/'
  resFromUrl = await axios.get(URL)
  links = [];
  let title = [];
  let time = [];
  let imgs=[];
  let description = [];
  $ = cheerio.load(resFromUrl.data)
  $('.clearfix')
  .each((index, element)=>{
    $(element)
    .find('article')
    .find('span')
    .each((index, element)=>{
      time.push($(element).text())
    })
  })
  .find('ul')
  .find('li')
  .find('a')
  .each((index, element)=>{
    $(element)
    .find('strong')
    .each((index, element)=>{
      title.push($(element).text())
    })
  })
  .find('div')
  .each((index, element)=>{
    imgs.push($(element).attr('data-lazy'))
  })
  const urlsImgId1 = title.filter(n => n != undefined )
  const urlsImgId2 = imgs.filter(n => n != undefined )
  //const urlsImgId3 = time.filter(n => n != undefined )



  return {
    props: {
      props1: urlsImgId1,
      props2: urlsImgId2,
      //props3: urlsImgId3
    }
  }  

}
