import Layout from './components/MyLayout';
import cheerio from 'cheerio';
import axios from 'axios';
import NameForm from './components/NameForm';
import React, {useState} from 'react';


const layoutStyle = {
  marginTop: 10,
  marginBottom: 20,
  padding: 20,
  border: '1px solid #DDD'
};



// client side
export default (props) => {
  const [message, setMessage] = useState('')
  const callbackFunction = (childData) => {
    setMessage(childData)
  };
  // console.log(props)
  let key=0;
  return (
    <Layout content={
      <div style={layoutStyle}>
        <NameForm parentCallback={callbackFunction}/>
        {message}
        <h2>Result</h2>
        {props.urlsImg.map((url)=>(
          <img src={url} alt="" key={key++}/>
        ))}
      </div>
    }/>  
  )
}

// server side
export async function getServerSideProps({message}){
  let message1 = 'https://tuoitre.vn/kinh-doanh.htm'
  let URL = message1//'https://tuoitre.vn/kinh-doanh.htm'
  if(message){
    URL = message
    location.reload();
  }
  const resFromUrl = await axios.get(URL)

  const $ = cheerio.load(resFromUrl.data)
  let links = [];
  $('.list-news-content')
    .find('li.news-item')
    .find('a.pos-rlt')
    .find('img.img212x132')
    .each((index, element)=>{
      links.push($(element).attr("src"))
    })
  const urlsImg = links.filter(n => n != undefined )
  return {
    props: {urlsImg}
  }  

}
